// Procesar consulta general al manual
const message = $input.first().json.message;
const chatId = message.chat.id;
const userQuery = message.text;
const userId = message.from.id;
const userName = message.from.first_name || 'Usuario';

// Detectar autom√°ticamente la secci√≥n m√°s relevante
function detectSection(query) {
  const q = query.toLowerCase();

  // Palabras clave por secci√≥n
  const sectionKeywords = {
    'introduccion': ['epp', 'seguridad', 'protecci√≥n personal', 'normas', 'programa', 'dc-82', 'formulario'],
    'equipos': ['aspiradora', 'escalera', 'herramienta', 'filtro', 'limpieza', 'port√°til', 'el√©ctrica'],
    'edificios': ['emergencia', 'inspecci√≥n', 'techo', 'puerta', 'ventana', 'estructura', 'edificio'],
    'electricos': ['el√©ctrico', 'luminaria', 'tablero', 'distribuci√≥n', 'alumbrado', 'electricidad'],
    'electronicos': ['audio', 'video', 'sonido', 'electr√≥nico', 'seguridad', 'sistema'],
    'mecanicos': ['aire acondicionado', 'climatizaci√≥n', 'agua', 'dispensador', 'ventilaci√≥n', 'mec√°nico', 'gotea'],
    'reparaciones': ['reparaci√≥n', 'r√°pida', 'emergencia', 'correctivo', '√≥xido', 'temporal']
  };

  let maxMatches = 0;
  let detectedSection = 'general';

  Object.entries(sectionKeywords).forEach(([section, keywords]) => {
    const matches = keywords.filter(keyword => q.includes(keyword)).length;
    if (matches > maxMatches) {
      maxMatches = matches;
      detectedSection = section;
    }
  });

  return detectedSection;
}

const detectedSection = detectSection(userQuery);

return {
  chat_id: chatId,
  user_id: userId,
  user_name: userName,
  query: userQuery,
  detected_section: detectedSection,
  timestamp: new Date().toISOString()
};

// Formatear respuesta exitosa del RAG
const input = $input.first().json;
const apiResponse = input.response;
const queryData = $('Procesar Consulta').first().json;

const chatId = queryData.chat_id;
const userQuery = queryData.query;
const userName = queryData.user_name;
const detectedSection = queryData.detected_section;

// Mapear nombres de secciones a emojis
const sectionEmojis = {
  'introduccion': 'ü¶∫',
  'equipos': 'üîß', 
  'edificios': 'üè¢',
  'electricos': '‚ö°',
  'electronicos': 'üì∫',
  'mecanicos': '‚ùÑÔ∏è',
  'reparaciones': 'üî®',
  'general': 'üìñ'
};

const sectionNames = {
  'introduccion': 'Seguridad y EPP',
  'equipos': 'Equipos',
  'edificios': 'Edificios', 
  'electricos': 'Sistemas El√©ctricos',
  'electronicos': 'Sistemas Electr√≥nicos',
  'mecanicos': 'Sistemas Mec√°nicos',
  'reparaciones': 'Reparaciones R√°pidas',
  'general': 'Manual General'
};

const emoji = sectionEmojis[detectedSection] || 'üìñ';
const sectionName = sectionNames[detectedSection] || 'Manual General';

// Construir mensaje principal
let message = `${emoji} *${sectionName.toUpperCase()}*\n\n`;
message += `*Consulta:* ${userQuery}\n\n`;
message += `${apiResponse.answer}\n\n`;

// Agregar informaci√≥n de fuentes si est√° disponible
if (apiResponse.sources && apiResponse.sources.length > 0) {
  message += `üìö *Referencias:*\n`;
  apiResponse.sources.forEach((source, index) => {
    if (source.page) {
      message += `‚Ä¢ P√°gina ${source.page}`;
      if (source.section) {
        message += ` - ${source.section}`;
      }
      message += `\n`;
    }
  });
  message += `\n`;
}

// Agregar tiempo de procesamiento
if (apiResponse.processing_time) {
  message += `‚è±Ô∏è *Tiempo:* ${apiResponse.processing_time.toFixed(2)}s\n\n`;
}

message += `üí° *¬øNecesitas m√°s informaci√≥n?* Escribe otra consulta o usa /help para ver ejemplos.`;

// Preparar respuesta base
const response = {
  chat_id: chatId,
  text: message,
  parse_mode: 'Markdown'
};

// Agregar informaci√≥n de im√°genes si est√°n disponibles
response.has_images = apiResponse.images && apiResponse.images.length > 0;
response.images = apiResponse.images || [];
response.api_response = apiResponse;

return response;

// Manejar error de API
const queryData = $('Procesar Consulta').first().json;
const chatId = queryData.chat_id;
const userQuery = queryData.query;

// Verificar tipo de error
const error = $input.first().json.error || {};
const isTimeout = error.code === 'ECONNABORTED' || error.message?.includes('timeout');
const isServerError = error.code >= 500 || error.message?.includes('Internal Server Error');

let errorMessage;

if (isTimeout) {
  errorMessage = `‚è≥ *Servicio inici√°ndose...*\n\nEl sistema est√° arrancando (cold start). Por favor, espera unos momentos y vuelve a intentarlo.\n\n*Tu consulta:* ${userQuery}\n\nüí° *Tip:* Los primeros arranques pueden tomar 30-60 segundos.`;
} else if (isServerError) {
  errorMessage = `üîß *Mantenimiento temporal*\n\nEl sistema de consultas est√° en mantenimiento. Por favor, intenta nuevamente en unos minutos.\n\n*Tu consulta:* ${userQuery}\n\nüìû Si el problema persiste, contacta al administrador.`;
} else {
  errorMessage = `‚ùå *Error de conexi√≥n*\n\nNo pude conectarme con la base de conocimientos del manual.\n\n*Tu consulta:* ${userQuery}\n\nüîÑ *Soluciones:*\n‚Ä¢ Verifica tu conexi√≥n\n‚Ä¢ Intenta de nuevo en unos momentos\n‚Ä¢ Usa /help para ver comandos disponibles`;
}

return {
  chat_id: chatId,
  text: errorMessage,
  parse_mode: 'Markdown'
};

// Enviar mensaje con im√°genes
const input = $input.first().json;
const images = input.images || [];
const chatId = input.chat_id;
const textMessage = input.text;

// Preparar array de respuestas
const responses = [];

// 1. Enviar mensaje de texto principal
responses.push({
  type: 'text',
  chat_id: chatId,
  text: textMessage,
  parse_mode: 'Markdown'
});

// 2. Enviar hasta 3 im√°genes relevantes
const maxImages = Math.min(images.length, 3);
for (let i = 0; i < maxImages; i++) {
  const image = images[i];

  let caption = `üì∏ Imagen ${i + 1}/${maxImages}`;
  if (image.description) {
    caption += `\n${image.description}`;
  }
  if (image.page) {
    caption += `\nüìÑ P√°gina: ${image.page}`;
  }

  responses.push({
    type: 'photo',
    chat_id: chatId,
    photo: image.url,
    caption: caption,
    parse_mode: 'Markdown'
  });
}

return responses;
